import React, {Component} from 'react';
import GoogleMapReact from 'google-map-react';
import ReactDOMServer from "react-dom/server";

const InfoWindowContent = (
  <div>
    <div id="content">
      <div id="siteNotice" />
      <h1 id="firstHeading" className="firstHeading">
        Uluru
      </h1>
      <div id="bodyContent">
        <p>
          <b>Uluru</b>, also referred to as <b>Ayers Rock</b>, is a large
          sandstone rock formation in the southern part of the Northern
          Territory, central Australia. It lies 335&#160;km (208&#160;mi) south
          west of the nearest large town, Alice Springs; 450&#160;km
          (280&#160;mi) by road. Kata Tjuta and Uluru are the two major features
          of the Uluru - Kata Tjuta National Park. Uluru is sacred to the
          Pitjantjatjara and Yankunytjatjara, the Aboriginal people of the area.
          It has many springs, waterholes, rock caves and ancient paintings.
          Uluru is listed as a World Heritage Site.
        </p>
        <p>
          Attribution: Uluru,
          <a href="https://en.wikipedia.org/w/index.php?title=Uluru&oldid=297882194">
            https://en.wikipedia.org/w/index.php?title=Uluru
          </a>
          (last visited June 22, 2009).
        </p>
      </div>
    </div>
  </div>
);

class SimpleMap extends Component {
  constructor(props) {
    super(props)
    this.state = {
      myLatLng : {
        lat: -37.813629,
        lng: 144.963058
      },
      zoom:13
    }
  }

  static defaultProps = {
    center: {
      lat: -37.813629,
      lng: 144.963058
    },
    zoom: 11
  };
  renderMarkers(map, maps, position) {
    var infowindow = new maps.InfoWindow({
      content:''
    });
    let marker = new maps.Marker({
      position: position,
      map,
      title: 'Hello World!',

    });
      marker.addListener('click', function() {
        const content = ReactDOMServer.renderToString(InfoWindowContent);
        infowindow.setContent(content); 
        infowindow.open(map, marker);
  })
  }
  componentDidMount(){
    console.log(this.props)
    if(this.props.match.params.id){
      let url='http://localhost:8000/share/'+this.props.match.params.id
      fetch(url)
      .then(results => {
        return results.json();
      })
      .then(data => {
        this.setState({myLatLng:{
          lat: data.location.coordinates[1],
          lng: data.location.coordinates[0]
        }});
        console.log(data)
      })

    }

  }
  render (){
  return (
     // Important! Always set the container height explicitly
     <div style={{ height: '100vh', width: '100%' }}>
     <GoogleMapReact
       bootstrapURLKeys={{ key: 'AIzaSyAtdOKKhJNvoNFVMIy20Upt7OLv1lHZJaI' }}
       onGoogleApiLoaded={({map, maps}) => this.renderMarkers(map, maps)}
       defaultCenter={this.props.center}
       center={this.state.myLatLng}
       yesIWantToUseGoogleMapApiInternals
       defaultZoom={this.state.zoom}
     >
     </GoogleMapReact>
   </div>
  );
}
}

export default SimpleMap;
